import React, { useState } from 'react'

const Buttons = (props) => {

  return (
    <div>
      <button onClick={() => {
        console.log('moi1')
        {props.setGood(props.good + 1)}
      } }>good</button>
      <button onClick={() => {
        console.log('moi')
        {props.setNeutral(props.neutral + 1)}
        } }>neutral</button>
      <button onClick={() => {
        console.log('moi')
        {props.setBad(props.bad + 1)}
        } }>bad</button>
    </div>
  )
}

const Statistics = (props) => {
  const all = ( props.good + props.neutral + props.bad )
  const avg = ( props.good * 1 + props.neutral * 0 + props.bad * -1 ) / ( props.good + props.neutral + props.bad )
  const positive = ( props.good / ( props.good + props.neutral + props.bad ) ) * 100
  return (
    <div>
      <p>good {props.good}</p>
      <p>neutral {props.neutral}</p>
      <p>bad {props.bad}</p>
      <p>all {all}</p>
      <p>average {avg}</p>
      <p>positive {positive} %</p>
    </div>
  )
}

const MoreStatistics = (props) => {
  const all = ( props.good + props.neutral + props.bad )
  const avg = ( props.good * 1 + props.neutral * 0 + props.bad * -1 ) / ( props.good + props.neutral + props.bad )
  const positive = ( props.good / ( props.good + props.neutral + props.bad ) ) * 100
  return (
    <div>
      <p>all {all}</p>
      <p>average {avg}</p>
      <p>positive {positive} %</p>
    </div>
  )
}

const App = () => {
  // tallenna napit omaan tilaansa
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  return (
    <div>
      <h1>give feedback</h1>
      <Buttons good={good} setGood={setGood} neutral={neutral} setNeutral={setNeutral} bad={bad} setBad={setBad}/>
      <h1>statistics</h1>
      <Statistics good={good} neutral={neutral} bad={bad}/>
      <MoreStatistics good={good} neutral={neutral} bad={bad}/>
    </div>
  )
}

export default App
