import React from 'react'


const Header = (props) => {
  return (
    <h1>
      {props.course}
    </h1>
  )
}

const Part = (props) => {
  return (
    <p>{props.part} {props.exercises}</p>
  )
}

const Content = (props) => {
  return (
    <div>
      {console.log('menos map')}
      {props.parts.map((p, i) => {
        console.log('p', p)
        return <Part part={p[i].name} exercises={p[i].exercises} />
      })}

      <Part part={props.parts[0].name} exercises={props.parts[0].exercises} />
      <Part part={props.parts[1].name} exercises={props.parts[1].exercises} />
      <Part part={props.parts[2].name} exercises={props.parts[2].exercises} />

    </div>
  )
}

const Total = (props) => {
  const total = props.parts.reduce((s, p) => {
    return s + p.exercises
  }, 0)
  return (
    <p><b>Number of exercises {total}</b></p>
  )
}

const Course = (props) => {
  const courses = props.courses
  return (
    <div>
      <p>moi</p>
      {courses.foreach((c, i) => {
        <div>

          {console.log('name', c.name)}
          <Header course={c.name} />
          <Content parts={c.parts} />
          <Total parts={c.parts} />

        </div>
        console.log('moi', c, i)})}

    </div>
  )
}

const App = () => {
  const courses = [
    {
      name: 'Half Stack application development',
      id: 1,
      parts: [
        {
          name: 'Fundamentals of React',
          exercises: 10,
          id: 1
        },
        {
          name: 'Using props to pass data',
          exercises: 7,
          id: 2
        },
        {
          name: 'State of a component',
          exercises: 14,
          id: 3
        },
        {
          name: 'Redux',
          exercises: 11,
          id: 4
        }
      ]
    },
    {
      name: 'Node.js',
      id: 2,
      parts: [
        {
          name: 'Routing',
          exercises: 3,
          id: 1
        },
        {
          name: 'Middlewares',
          exercises: 7,
          id: 2
        }
      ]
    }
  ]


  return (
    <div>
      <Course courses={courses} />
    </div>
  )
}

export default App
